import React, { useState } from 'react';
import ReactDOM from 'react-dom';

const App = ({ anecdotes }) => {
  const [selected, setSelected] = useState(0);
  const [votes, setVotes] = useState(points);

  const handleNext = () => {
    setSelected(getRandomIndex(anecdotes.length));
  };

  const handleVote = () => {
    setVotes({ ...votes, [selected]: votes[selected] + 1 });
  };

  const mostVotedIndex = findMaxPointIndex(arrayFrom(votes));

  return (
    <div>
      <h1>Anecdote of the day</h1>
      <Anecdote content={anecdotes[selected]} votes={votes[selected]} />
      <button onClick={handleVote}>Vote</button>
      <button onClick={handleNext}>Next anecdote</button>
      <h2>Anecdote with most votes</h2>
      <Anecdote
        content={anecdotes[mostVotedIndex]}
        votes={votes[mostVotedIndex]}
      />
    </div>
  );
};

const Anecdote = ({ content, votes }) => (
  <div>
    <p>{content}</p>
    <p>has {votes} votes</p>
  </div>
);

const anecdotes = [
  'If it hurts, do it more often',
  'Adding manpower to a late software project makes it later!',
  'The first 90 percent of the code accounts for the first 90 percent of the development time...The remaining 10 percent of the code accounts for the other 90 percent of the development time.',
  'Any fool can write code that a computer can understand. Good programmers write code that humans can understand.',
  'Premature optimization is the root of all evil.',
  'Debugging is twice as hard as writing the code in the first place. Therefore, if you write the code as cleverly as possible, you are, by definition, not smart enough to debug it.'
];

const points = { ...Array(anecdotes.length).fill(0) };

const getRandomIndex = length => Math.floor(Math.random() * length);

const arrayFrom = obj => Object.values(obj);

const getMaxPoint = arr => Math.max(...arr);

const findIndex = (value, arr) => arr.indexOf(value);

const findMaxPointIndex = arr => findIndex(getMaxPoint(arr), arr);

ReactDOM.render(<App anecdotes={anecdotes} />, document.getElementById('root'));
