import React, { useState,  useEffect }  from 'react'
import axios from 'axios'

import ReactDOM from 'react-dom'

const App = () => {
  const [ countries, setCountries] = useState([]) 
  const [ find_country, setFindCountries] = useState('') 

  useEffect(() => {
    axios
      .get('https://restcountries.eu/rest/v2/all')
      .then(response => {
        console.log(response.data)
        setCountries(response.data)
      })

  }, [])
  const handleFilter = (event) => {
    setFindCountries(event.target.value)
  }
  let result = countries.filter(country => country.name.toLowerCase().includes(find_country))
  return (
    <div>
      find countries: <input  
            value={find_country}
            onChange={handleFilter}/>
        {result.length < 10 ?
        <div>
          {result.length > 1 ?
            result.map((filterCountry,idx) => (
              <div key={idx}>
                <p>{filterCountry.name} </p>          
              </div>
            ))
          :
            result.map((filterCountry,idx) => (
              <div key={idx}>
                <h1>{filterCountry.name} </h1>          
                <p>capital {filterCountry.capital}</p>
              </div>
            ))
          } 
        </div>
        : 
          <p>Too many matches, specify another filter</p>
        }
    </div>
  )
}

ReactDOM.render(<App />, 
  document.getElementById('root')
)
