import React, { useState,  useEffect }  from 'react'
import axios from 'axios'

import ReactDOM from 'react-dom'

const App = () => {
  const [ countries, setCountries] = useState([]) 
  const [ find_country, setFindCountries] = useState('') 

  useEffect(() => {
    axios
      .get('https://restcountries.eu/rest/v2/all')
      .then(response => {
        console.log(response.data)
        setCountries(response.data)
      })

  }, [])
  const handleFilter = (event) => {
    console.log(event.target.value)
    setFindCountries(event.target.value)
  }
  return (
    <div>
      find countries: <input  
            value={find_country}
            onChange={handleFilter}/>
      {countries.filter(country => country.name.toLowerCase().includes(find_country)).length < 10 ? 
        countries.filter(country => country.name.toLowerCase().includes(find_country)).map(filterCountry => (
          <p>
            {filterCountry.name}           
          </p>
        ))
        : 
          <p>Too many matches, specify another filter</p>
        }
    </div>
  )
}

ReactDOM.render(<App />, 
  document.getElementById('root')
)
