import React, { useState,  useEffect }  from 'react'
import axios from 'axios'
import FilterCountry from './component/FilterCountry'
import ReactDOM from 'react-dom'

const App = () => {
  const [ countries, setCountries] = useState([]) 
  const [ find_country, setFindCountries] = useState('') 
  const [count, setCount] = useState('');

  useEffect(() => {
    axios
      .get('https://restcountries.eu/rest/v2/all')
      .then(response => {
        setCountries(response.data)
      })

  }, [])
  const handleFilter = (newValue) => {
    setFindCountries(newValue)
    setCount('')
  }


  let result = countries.filter(country => country.name.toLowerCase().includes(find_country))
  return (
    <div>
      <FilterCountry  value={find_country} onChange={handleFilter}/>

      {result.length < 10 ?
        <div>
          {result.length > 1 ?
            result.map((filterCountry,idx) => (
              <div key={idx} id={idx}>
                <p>{filterCountry.name} <span><button value={idx} onClick={(e) => setCount(e.target.value)}>show</button></span></p>       
                {idx === parseInt(count) ?
                  <div>
                    <h1>{filterCountry.name} </h1>          
                    <p>capital {filterCountry.capital}</p>
                    <p>population {filterCountry.population}</p>
                    <h2>languages</h2>
                    <ul>
                      {filterCountry.languages.map((language,index)=>(
                        <li key={index}>
                            {language.name}
                        </li>
                      ))}
                    </ul>
                    <img style={{width:'250px', height:'250px'}} src={filterCountry.flag} alt="country_flag" />
                  </div>
                :
                  <div></div>
                }
              </div>
            ))
          :
            result.map((filterCountry,idx) => (
              <div key={idx}>
                <h1>{filterCountry.name} </h1>          
                <p>capital {filterCountry.capital}</p>
                <p>population {filterCountry.population}</p>
                <h2>languages</h2>
                <ul>
                  {filterCountry.languages.map((language,index)=>(
                    <li key={index}>
                        {language.name}
                    </li>
                  ))}
                </ul>
                <img style={{width:'250px', height:'250px'}} src={filterCountry.flag} alt="country_flag" />
              </div>
            ))
          } 
        </div>
        : 
          <p>Too many matches, specify another filter</p>
        }
    </div>
  )
}

ReactDOM.render(<App />, 
  document.getElementById('root')
)
