import React, { useState,  useEffect }  from 'react'
import axios from 'axios'

import ReactDOM from 'react-dom'

const App = () => {
  const [ countries, setCountries] = useState([]) 
  const [ find_country, setFindCountries] = useState('') 

  useEffect(() => {
    axios
      .get('https://restcountries.eu/rest/v2/all')
      .then(response => {
        setCountries(response.data)
      })

  }, [])
  const handleFilter = (event) => {
    setFindCountries(event.target.value)
  }
  let result = countries.filter(country => country.name.toLowerCase().includes(find_country))
  let text;
  switch(true){


    case result.length > 10:
      text = (
        <p>Too many matches, specify another filter</p>
      )

    case result.length > 1 && result.length < 10:
      text = (
        result.map((filterCountry,idx) => (
          <div key={idx}>
            <p>{filterCountry.name} <span><button>show</button></span></p>       

          </div>
        ))
      )

    case result.length === 1:
      text = (
        result.map((filterCountry,idx) => (
          <div key={idx}>
            <h1>{filterCountry.name} </h1>          
            <p>capital {filterCountry.capital}</p>
            <p>population {filterCountry.population}</p>
            <h2>languages</h2>
            <ul>
              {filterCountry.languages.map((language,index)=>(
                <li key={index}>
                    {language.name}
                </li>
              ))}
            </ul>
            <img style={{width:'250px', height:'250px'}} src={filterCountry.flag} alt="country_flag" />
          </div>
        ))
      )  
  }
  return (
    <div>
      find countries: <input  
            value={find_country}
            onChange={handleFilter}/>
        {text}
    </div>
  )
}

ReactDOM.render(<App />, 
  document.getElementById('root')
)
