import React from 'react'

const Header = (props) =>{
  return(
    <div>
      <h1>{props.header}</h1>
    </div>
  )
}

const Content = (props) =>{
  let sum = props.parts.map(part => part.exercises).reduce((a, c) => { return a + c });
  return(
    <div>
      {props.parts.map((part) => 
          <Part key={part.id} name={part.name} exercise={part.exercises} total={sum}/>
        )}
    </div>
  )
}

const Part = ({name, exercise, total}) =>{
   return(
     <div>
      <p>{name} {exercise}</p>
      <p><strong>Total of {total} exercises</strong></p>
     </div>
   )
}

const Course = (props) => {
    const { course } = props
    console.log(course)

    return (
      <div>
         <Header header={course.name} />
         <Content parts={course.parts}/>
      </div>
    )
}

export default Course
