import React, { useState} from 'react'
import ReactDOM from 'react-dom'

const Filter = (props) =>{
  const handleFilter = (event) => {
    props.onChange(event.target.value)
  }
  return(
    <div>
     filter shown with: <input  
            value={props.value}
            onChange={handleFilter}/>
    </div>
  ) 
}
const PersonForm = (props) =>{


  return (
    <div>
      <form onSubmit={addPerson}>
        <div>
          name: 
          <input  
            value={props.values.name}
            name="name"
            onChange={handleChange('name')}/>
        </div>
        <div>
          number: 
          <input  
            value={props.values.number}
            name="number"
            onChange={handleChange('number')}/>
          </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
    </div>
  )
}
const Persons = ({person,filter}) =>{
  return(
    <div>
     {person
          .filter(contact =>contact.name.toLowerCase().includes(filter))
          .map((contact,idx) => 
            <li key={idx}>{contact.name} {contact.number}</li>
        )}
    </div>
  )
}
const App = () => {
  const [ persons, setPersons ] = useState([
    { name: 'Arto Hellas', number: '040-123456' },
    { name: 'Ada Lovelace', number: '39-44-5323523' },
    { name: 'Dan Abramov', number: '12-43-234345' },
    { name: 'Mary Poppendieck', number: '39-23-6423122' },
  ]) 
  const [ newInfo, setNewInfo] = useState({
    name: "",
    number: ""
  })
  const [filterName, setFilterName] = useState('')

  const handleFilter = (newValue)=> {
    setFilterName(newValue)  

  }
  const handleChange = name => event => {
    setNewInfo({
      ...newInfo, [name]: event.target.value
    });
  }
  const addPerson = (event) => {
    event.preventDefault()
    if(newInfo.name !== undefined){
      if(persons.some(person => person.name === newInfo.name)){
        alert(`${newInfo.name} is already added to phonebook`);
      } 
      else{
        const personObject = {
          name: newInfo.name,
          number: newInfo.number
        }

        setPersons(persons.concat(personObject))
        setNewInfo('')
      }
    }
  }
  return (
    <div>
      <h2>Phonebook</h2>
      <Filter  value={filterName} onChange={handleFilter}/>
      <h2>add a new</h2>
      <PersonForm values={newInfo} onChange={handleChange} onSubmit={addPerson} />  
      <h2>Numbers</h2>
      <Persons person={persons} filter={filterName}/>

    </div>
  )
}

ReactDOM.render(<App />, 
  document.getElementById('root')
)
