import React, { useState } from 'react'
import ReactDOM from 'react-dom'


const App = () => {
  const [ persons, setPersons ] = useState([
    { name: 'Arto Hellas', number: '040-123456' },
  ]) 
  const [ newInfo, setNewInfo] = useState({
    name: "",
    number: ""
  })

  const handleNameChange = (event) => {
    console.log(event.target.value)
    setNewInfo(event.target.value)

  }
  const addPerson = (event) => {
    event.preventDefault()
    if(persons.some(person => person.name === newInfo.name)){
      alert(`${newInfo.name} is already added to phonebook`);
    } 
    else{
      const personObject = {
        name: newInfo.name,
        number: newInfo.number
      }

      setPersons(persons.concat(personObject))
      setNewInfo('')
    }

  }
  console.log(newInfo)
  return (
    <div>
      <h2>Phonebook</h2>
      <form onSubmit={addPerson}>
        <div>
          name: 
          <input  value={newInfo.name}
                  onChange={handleNameChange}/>
        </div>
        <div>
          number: 
          <input  value={newInfo.number}
                  onChange={handleNameChange}/>
          </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      <ul style={{paddingLeft:'0px'}}>
        {persons.map((person,idx) => 
          <li key={idx}>{person.name} {person.number}</li>
        )}
      </ul>
    </div>
  )
}

ReactDOM.render(<App />, 
  document.getElementById('root')
)
