import React, { useState} from 'react'
import ReactDOM from 'react-dom'

const Filter = ({ onChange, value }) =>{
  console.log(onChange)

  return(
    <div>
     filter shown with: 
    </div>
  ) 
}
const App = () => {
  const [ persons, setPersons ] = useState([
    { name: 'Arto Hellas', number: '040-123456' },
    { name: 'Ada Lovelace', number: '39-44-5323523' },
    { name: 'Dan Abramov', number: '12-43-234345' },
    { name: 'Mary Poppendieck', number: '39-23-6423122' },
  ]) 
  const [ newInfo, setNewInfo] = useState({
    name: "",
    number: ""
  })
  const [filterName, setFilterName] = useState('')

  const handleFilter = (event) => {
    setFilterName(event.target.value)  

  }
  const handleChange = (event) => {
    const value = event.target.value;
    setNewInfo({
      ...newInfo,
      [event.target.name]: value
    });
  }
  const addPerson = (event) => {
    event.preventDefault()
    if(newInfo.name !== undefined){
      if(persons.some(person => person.name === newInfo.name)){
        alert(`${newInfo.name} is already added to phonebook`);
      } 
      else{
        const personObject = {
          name: newInfo.name,
          number: newInfo.number
        }

        setPersons(persons.concat(personObject))
        setNewInfo('')
      }
    }
  }
  return (
    <div>
      <h2>Phonebook</h2>
      <Filter  value={filterName}
            onChange={handleFilter}/>
      <h2>add a new</h2>

      <form onSubmit={addPerson}>
        <div>
          name: 
          <input  
            value={newInfo.name}
            name="name"
            onChange={handleChange}/>
        </div>
        <div>
          number: 
          <input  
            value={newInfo.number}
            name="number"
            onChange={handleChange}/>
          </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      <ul style={{paddingLeft:'0px'}}>
        {persons
          .filter(person =>person.name.toLowerCase().includes(filterName))
          .map((person,idx) => 
            <li key={idx}>{person.name} {person.number}</li>
        )}
      </ul>
    </div>
  )
}

ReactDOM.render(<App />, 
  document.getElementById('root')
)
